class GessehTerrain extends BaseGessehTerrain
{
  public function __toString()
  {
    if ($this->getId() == null)
      return "Aucun";
    else
      return sprintf('%s : %s (%s)', $this->getGessehHopital()->getTitre(), $this->getTitre(), $this->getPatron());
  }

  /* Retourne le nombre de 1er voeux pour le terrain */
  public function getPostesRequis()
  {
    $voeux = Doctrine::getTable('GessehChoix')->getFirstChoixByTerrain($this->getId());

    return $voeux->count();
  }

}
